package Lec1;

public class Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 9;
		int b = 7;
		int c = a + b;
		System.out.println(c);
		c = a - b;
		System.out.println(c);
		System.out.println(a++);// a= a+1;
		System.out.println(a);
		System.out.println(b--);
		System.out.println(b);

	}

}
